// QuotationPDFGenerator.ts
import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";

const actualLogoBase64 = "data:image/png;base64,";

const actualSignatureBase64 = "data:image/png;base64,";

export interface QuotationItem {
  id: string;
  slNo: number;
  productName: string;
  productCode: string;
  size: string;
  specification: string;
  category?: string;
  combo?: string;
  unitPrice: number;
  quantity: number;
  total: number;
  customBadgeText?: string;
  addOnsProducts?: QuotationItem[];
}

export interface BankDetailsPDF {
  unitName: string;
  bankName: string;
  branchName: string;
  accountNo: string;
  accountType: string;
  micr: string;
  ifsc: string;
  id?: string;
}

// --- THE FIX IS HERE: Updated QuotationData Interface ---
export interface QuotationData {
  companyName: string;
  subtitle: string;
  gstin: string;
  tagline: string;
  address: string;
  area: string;
  sales: string;
  service: string;
  website: string;
  email: string;
  factory: string;

  // Customer Details
  customerName: string;
  customerGST: string;
  customerPhone: string;
  customerEmail: string; // NEW
  customerAddress1: string; // NEW
  customerAddress2: string; // NEW
  customerCity: string; // NEW
  customerState: string; // NEW
  customerZip: string; // NEW
  customerCountry: string; // NEW
  customerLocation: string; // Kept for PDF generation

  // Order Details
  refNo: string;
  date: string;
  items: QuotationItem[];
  totalBeforeDiscount: number;
  discount: number;
  totalAfterDiscount: number;
  cgst: number;
  sgst: number;
  igst?: number;
  grandTotal: number;
  discountPercent: number;
  
  // Bank & Terms
  bankDetails: BankDetailsPDF;
  termsAndConditions?: { term: string; details: string; id?: string }[];
  templateType: "proforma" | "estimation";
}

export const QuotationPDFGenerator = (data: QuotationData): jsPDF | null => {
  try {
    const documentTitle =
      data.templateType === "proforma" ? "Proforma Invoice" : "Estimation";
    const doc = new jsPDF({ unit: "mm", format: "a4" });
    doc.setProperties({
      title: `${documentTitle} - ${data.refNo}`,
      subject: `${documentTitle} - ${data.subtitle || ""}`,
      author: data.companyName,
      creator: "Checkout System",
    });

    let currentPageNumber = 1;
    const pageHeight = doc.internal.pageSize.getHeight();
    const pageWidth = doc.internal.pageSize.getWidth();

    const leftMargin = 14;
    const rightMargin = 14;
    const topMarginPage = 10;
    const bottomMargin = 15;
    const footerTextHeight = 5;
    const tableSpacing = 7;

    const addPageNumber = (docInstance: jsPDF, pageNum: number) => {
      const originalFontSize = docInstance.getFontSize();
      const originalFont = docInstance.getFont();
      docInstance.setFontSize(8);
      docInstance.setFont("helvetica", "normal");
      docInstance.text(
        `Page ${pageNum}`,
        pageWidth / 2,
        pageHeight - bottomMargin / 2,
        { align: "center" }
      );
      docInstance.setFontSize(originalFontSize);
      docInstance.setFont(originalFont.fontName, originalFont.fontStyle);
    };

    let didAutoTableDrawFooterOnLastPage = false;

    const checkAndAddPage = (
      currentYPos: number,
      requiredSpace: number = 0
    ): number => {
      if (
        currentYPos + requiredSpace >
        pageHeight - bottomMargin - footerTextHeight
      ) {
        if (currentPageNumber > 0 && !didAutoTableDrawFooterOnLastPage) {
          addPageNumber(doc, currentPageNumber);
        }
        doc.addPage();
        currentPageNumber++;
        didAutoTableDrawFooterOnLastPage = false;
        return topMarginPage;
      }
      return currentYPos;
    };

    let currentContentY = topMarginPage;

    // --- Header, Logo, Company Info, Recipient (Unchanged) ---
    doc.setFontSize(8);
    doc.setTextColor(0, 0, 0);
    doc.setFont("helvetica", "normal");
    const gstinText = `GSTIN: ${data.gstin}`;
    doc.text(
      gstinText,
      pageWidth - rightMargin - doc.getTextWidth(gstinText),
      currentContentY + 3
    );

    const logoAvailableWidth = pageWidth - leftMargin - rightMargin;
    const logoHeight = 25;
    const logoX = leftMargin;
    const logoY = 10;
    try {
      if (actualLogoBase64) {
        doc.addImage(
          actualLogoBase64,
          "PNG",
          logoX,
          logoY,
          logoAvailableWidth,
          logoHeight
        );
      } else {
        throw new Error("Logo Base64 is empty.");
      }
    } catch (e) {
      console.error("Error adding logo image:", e);
      doc.setFontSize(10);
      doc.text("Logo Area", pageWidth / 2, logoY + logoHeight / 2, {
        align: "center",
      });
    }

    currentContentY = logoY + logoHeight + 7;
    doc.setFontSize(8);
    let companyDetailsY = currentContentY;
    if (data.tagline) {
      doc.setTextColor(100, 100, 100);
      doc.text(data.tagline, leftMargin, companyDetailsY);
      companyDetailsY += 5;
      doc.setTextColor(0, 0, 0);
    }
    doc.text(`GSTIN: ${data.gstin}`, leftMargin, companyDetailsY);
    companyDetailsY += 5;
    doc.text(
      `Registered Office & Showroom: ${data.address}`,
      leftMargin,
      companyDetailsY
    );
    companyDetailsY += 5;
    doc.text(data.area, leftMargin, companyDetailsY);
    companyDetailsY += 5;
    doc.text(
      `Sales: ${data.sales} / Service: ${data.service}`,
      leftMargin,
      companyDetailsY
    );
    companyDetailsY += 5;
    doc.text(`${data.website} | ${data.email}`, leftMargin, companyDetailsY);
    companyDetailsY += 7;
    doc.line(
      leftMargin,
      companyDetailsY,
      pageWidth - rightMargin,
      companyDetailsY
    );

    const recipientInfoStartY = companyDetailsY + 5;
    const recipientBoxWidth = (pageWidth - leftMargin - rightMargin - 4) * 0.6;
    const refBoxWidth = (pageWidth - leftMargin - rightMargin - 4) * 0.4;
    const recipientBoxHeight = 35;
    doc.rect(
      leftMargin,
      recipientInfoStartY,
      recipientBoxWidth,
      recipientBoxHeight
    );

    let currentRecipientY = recipientInfoStartY + 6;
    const lineSpacing = 5;
    doc.setFontSize(9);
    doc.text("To:", leftMargin + 2, currentRecipientY);
    currentRecipientY += lineSpacing;
    doc.setFont("helvetica", "bold");
    doc.text(data.customerName, leftMargin + 2, currentRecipientY);
    currentRecipientY += lineSpacing;
    doc.setFont("helvetica", "normal");
    const addressLines = doc.splitTextToSize(
      data.customerLocation,
      recipientBoxWidth - 4
    );
    doc.text(addressLines, leftMargin + 2, currentRecipientY);
    currentRecipientY += addressLines.length * (lineSpacing - 1);
    if (data.customerPhone && data.customerPhone !== "N/A") {
      doc.text(
        `Phone: ${data.customerPhone}`,
        leftMargin + 2,
        currentRecipientY
      );
    }

    doc.rect(
      leftMargin + recipientBoxWidth + 4,
      recipientInfoStartY,
      refBoxWidth,
      recipientBoxHeight
    );
    doc.text(
      `Date: ${data.date}`,
      leftMargin + recipientBoxWidth + 4 + 2,
      recipientInfoStartY + 6
    );
    doc.text(
      `REF: ${data.refNo}`,
      leftMargin + recipientBoxWidth + 4 + 2,
      recipientInfoStartY + 12
    );

    const quotationTitleY = recipientInfoStartY + recipientBoxHeight + 10;
    doc.setFontSize(12);
    doc.setFont("helvetica", "bold");
    doc.text(documentTitle, pageWidth / 2, quotationTitleY, {
      align: "center",
    });
    currentContentY = quotationTitleY + 5;

    // --- TABLE GENERATION (MODIFIED) ---
    const formatCurrencyForTable = (value: number) =>
      `Rs. ${value.toLocaleString("en-IN", {
        minimumFractionDigits: 2,
        maximumFractionDigits: 2,
      })}`;
    const tableColumn = [
      "S.No",
      "Product Details",
      "Size",
      "Unit Price",
      "Qty",
      "Total Amount",
    ];
    
    const tableRowsData: any[] = [];
    
    const badgeRowStyle = {
      halign: "left" as const,
      fontStyle: "italic",
      fillColor: [245, 245, 245],
      textColor: 50,
      fontSize: 7,
      cellPadding: { top: 1, bottom: 1, left: 2 },
    };

    data.items.forEach((mainItem) => {
      if (mainItem.customBadgeText) {
        tableRowsData.push([
          {
            content: mainItem.customBadgeText,
            colSpan: 6,
            styles: badgeRowStyle,
          },
        ]);
      }

      const mainProductDetails = [mainItem.productName];
      if (mainItem.productCode) mainProductDetails.push(`Code: ${mainItem.productCode}`);
      if (mainItem.category) mainProductDetails.push(`Category: ${mainItem.category}`);
      if (mainItem.combo) mainProductDetails.push(`Combo: ${mainItem.combo}`);
      if (mainItem.specification) mainProductDetails.push(`Spec: ${mainItem.specification}`);

      tableRowsData.push([
        mainItem.slNo.toString(),
        mainProductDetails.join("\n"),
        mainItem.size,
        formatCurrencyForTable(mainItem.unitPrice),
        mainItem.quantity,
        formatCurrencyForTable(mainItem.total),
      ]);

      if (mainItem.addOnsProducts && mainItem.addOnsProducts.length > 0) {
        tableRowsData.push([
          {
            content: "Associated Add-On Products",
            colSpan: 6,
            styles: { ...badgeRowStyle, fontStyle: 'bold' },
          },
        ]);
        mainItem.addOnsProducts.forEach((addOn) => {
          if (addOn.customBadgeText) {
            tableRowsData.push([
              {
                content: addOn.customBadgeText,
                colSpan: 6,
                styles: badgeRowStyle,
              },
            ]);
          }

          const addOnDetails = [` ${addOn.productName}`];
          if (addOn.productCode) addOnDetails.push(`  Code: ${addOn.productCode}`);
          if (addOn.specification) addOnDetails.push(`  Spec: ${addOn.specification}`);
          
          tableRowsData.push([
            "", 
            addOnDetails.join("\n"),
            addOn.size,
            formatCurrencyForTable(addOn.unitPrice),
            addOn.quantity,
            formatCurrencyForTable(addOn.total),
          ]);
        });
      }
    });
    
    const tableAvailableWidth = pageWidth - leftMargin - rightMargin;
    const cellPaddingValue = 1.5;
    const totalHorizontalPaddingPerCell = cellPaddingValue * 2;
    const totalPaddingForAllCells = tableColumn.length * totalHorizontalPaddingPerCell;
    const contentWidthForCells = tableAvailableWidth - totalPaddingForAllCells;

    autoTable(doc, {
      head: [tableColumn],
      body: tableRowsData,
      startY: currentContentY,
      margin: {
        top: topMarginPage,
        right: rightMargin,
        left: leftMargin,
        bottom: bottomMargin + footerTextHeight + 5,
      },
      styles: {
        fontSize: 8,
        cellPadding: cellPaddingValue,
        lineColor: [153, 153, 153],
        lineWidth: 0.1,
        overflow: "linebreak",
      },
      headStyles: {
        fillColor: [220, 220, 220],
        textColor: [0, 0, 0],
        fontStyle: "bold",
      },
      columnStyles: {
        0: { cellWidth: 10, halign: "center" },
        1: { cellWidth: contentWidthForCells * 0.4 },
        2: { cellWidth: contentWidthForCells * 0.15, halign: "left" },
        3: { cellWidth: contentWidthForCells * 0.15, halign: "right" },
        4: { cellWidth: contentWidthForCells * 0.1, halign: "center" },
        5: { cellWidth: contentWidthForCells * 0.2, halign: "right" },
      },
      didDrawPage: function (hookData: any) {
        if (hookData.pageNumber > currentPageNumber || !didAutoTableDrawFooterOnLastPage || (hookData.pageNumber === 1 && !didAutoTableDrawFooterOnLastPage)) {
          addPageNumber(doc, hookData.pageNumber);
          didAutoTableDrawFooterOnLastPage = true;
        }
        currentPageNumber = hookData.pageNumber;
      },
      willDrawPage: function () {
        didAutoTableDrawFooterOnLastPage = false;
      },
    });

    const lastTable = (doc as any).lastAutoTable;
    currentContentY = lastTable?.finalY
      ? lastTable.finalY + tableSpacing
      : currentContentY;

    // ... (All code for Summary Block, Bank Details, T&C, and Footer is unchanged)
    // ...
    const paddingAfterElement = 5;
    currentContentY = Math.max(
      currentContentY - tableSpacing + paddingAfterElement,
      currentContentY
    );
    currentContentY = checkAndAddPage(currentContentY);

    const formatCurrencySummary = (value: number) =>
      `Rs. ${value.toLocaleString("en-IN", {
        minimumFractionDigits: 2,
        maximumFractionDigits: 2,
      })}`;
    const summaryTotalWidth = Math.max(80, refBoxWidth);
    const summaryXStart = pageWidth - rightMargin - summaryTotalWidth;
    const summaryLabelWidthRatio = 0.65;
    const summaryValueWidthRatio = 0.35;
    const summaryItemHeight = 7;

    const summaryItems = [
      {
        label: "Subtotal (Before Discount)",
        value: data.totalBeforeDiscount,
        bold: false,
        size: 9,
      },
      {
        label: `Discount`,
        value: data.discount,
        bold: false,
        size: 9,
        isDiscount: true,
      },
      {
        label: "Total (After Discount)",
        value: data.totalAfterDiscount,
        bold: false,
        size: 9,
      },
    ];

    if (data.cgst > 0 && data.sgst > 0) {
      summaryItems.push({
        label: `CGST (${
          data.totalAfterDiscount > 0
            ? ((data.cgst / data.totalAfterDiscount) * 100).toFixed(2)
            : 0
        }%)`,
        value: data.cgst,
        bold: false,
        size: 9,
      });
      summaryItems.push({
        label: `SGST (${
          data.totalAfterDiscount > 0
            ? ((data.sgst / data.totalAfterDiscount) * 100).toFixed(2)
            : 0
        }%)`,
        value: data.sgst,
        bold: false,
        size: 9,
      });
    } else if (data.igst && data.igst > 0) {
      summaryItems.push({
        label: `IGST (${
          data.totalAfterDiscount > 0
            ? ((data.igst / data.totalAfterDiscount) * 100).toFixed(2)
            : 0
        }%)`,
        value: data.igst,
        bold: false,
        size: 9,
      });
    }

    summaryItems.push({
      label: "GRAND TOTAL",
      value: data.grandTotal,
      bold: true,
      size: 10,
    });

    doc.setFont("helvetica", "normal");
    summaryItems.forEach((item) => {
      currentContentY = checkAndAddPage(currentContentY, summaryItemHeight + 1);
      const labelW = summaryTotalWidth * summaryLabelWidthRatio;
      const valueW = summaryTotalWidth * summaryValueWidthRatio;
      doc.rect(summaryXStart, currentContentY, labelW, summaryItemHeight);
      doc.rect(
        summaryXStart + labelW,
        currentContentY,
        valueW,
        summaryItemHeight
      );
      doc.setFontSize(item.size);
      doc.setFont("helvetica", item.bold ? "bold" : "normal");
      const textYOffset = summaryItemHeight / 2 + item.size / 7;
      doc.text(item.label, summaryXStart + 2, currentContentY + textYOffset);
      const valuePrefix = item.isDiscount && item.value > 0 ? "- " : "";
      doc.text(
        valuePrefix + formatCurrencySummary(item.value),
        summaryXStart + labelW + valueW - 2,
        currentContentY + textYOffset,
        { align: "right" }
      );
      currentContentY += summaryItemHeight;
    });
    doc.setFont("helvetica", "normal");

    const sectionSpacingLarge = 8;
    currentContentY += sectionSpacingLarge;

    const contentLineHeight = 5;
    const titleHeight = 6;

    currentContentY = checkAndAddPage(currentContentY, titleHeight);
    doc.setFontSize(10);
    doc.setFont("helvetica", "bold");
    doc.text("DETAILS OF OUR BANKERS:", leftMargin, currentContentY);
    currentContentY += titleHeight;

    doc.setFont("helvetica", "normal");
    doc.setFontSize(9);
    const bankDetailsList = [
      `Name of the Unit: ${data.bankDetails.unitName || "N/A"}`,
      `Name of the Bank: ${data.bankDetails.bankName}`,
      `Name of the Branch: ${data.bankDetails.branchName || "N/A"}`,
      `Account No: ${data.bankDetails.accountNo}`,
      `Type of Account: ${data.bankDetails.accountType}`,
      `MICR: ${data.bankDetails.micr || "N/A"}`,
      `IFSC: ${data.bankDetails.ifsc}`,
    ];
    bankDetailsList.forEach((line) => {
      currentContentY = checkAndAddPage(currentContentY, contentLineHeight);
      doc.text(line, leftMargin, currentContentY);
      currentContentY += contentLineHeight;
    });

    if (data.termsAndConditions && data.termsAndConditions.length > 0) {
      currentContentY += sectionSpacingLarge;
      currentContentY = checkAndAddPage(currentContentY, titleHeight);
      doc.setFontSize(10);
      doc.setFont("helvetica", "bold");
      doc.text("TERMS AND CONDITIONS:", leftMargin, currentContentY);
      currentContentY += titleHeight;

      doc.setFontSize(9);
      const termIndent = leftMargin + 30;
      const termMaxWidth = pageWidth - termIndent - rightMargin;
      const termItemGap = 2;

      data.termsAndConditions.forEach((item) => {
        doc.setFont("helvetica", "bold");
        const termHeaderDimensions = doc.getTextDimensions(item.term);
        const termHeaderHeight = termHeaderDimensions.h + termItemGap;
        doc.setFont("helvetica", "normal");
        const detailLines = doc.splitTextToSize(item.details, termMaxWidth);
        const detailLineHeight = (9 / 72) * 25.4 * 1.15;
        const detailBlockHeight = detailLines.length * detailLineHeight;
        const requiredTermItemHeight =
          Math.max(termHeaderHeight, detailBlockHeight) + termItemGap;
        currentContentY = checkAndAddPage(
          currentContentY,
          requiredTermItemHeight
        );
        let itemStartY = currentContentY;
        const baseTextY = itemStartY + detailLineHeight / 2 + 1;
        doc.setFont("helvetica", "bold");
        doc.text(item.term, leftMargin, baseTextY);
        doc.setFont("helvetica", "normal");
        doc.text(detailLines, termIndent, baseTextY);
        currentContentY +=
          Math.max(termHeaderHeight - termItemGap, detailBlockHeight) +
          termItemGap;
      });
      doc.setFont("helvetica", "normal");
    }

    currentContentY += sectionSpacingLarge * 1.5;
    const signatoryBlockHeight = 25;
    currentContentY = checkAndAddPage(currentContentY, signatoryBlockHeight);

    const signatoryX = pageWidth - rightMargin - 60;
    const forCompanyNameY = currentContentY;
    const signatureImageY = forCompanyNameY + 5;
    const signatureImageHeight = 12;
    const signatureImageWidth = 35;
    const authorisedTextY = signatureImageY + signatureImageHeight + 3;

    doc.setFontSize(9);
    doc.setFont("helvetica", "normal");
    doc.text("For " + data.companyName, signatoryX, forCompanyNameY);

    try {
      if (actualSignatureBase64) {
        doc.addImage(
          actualSignatureBase64,
          "PNG",
          signatoryX,
          signatureImageY,
          signatureImageWidth,
          signatureImageHeight
        );
      } else {
        throw new Error("Signature Base64 is empty.");
      }
    } catch (e) {
      console.error("Error adding signature image:", e);
      doc.text(
        "Signature Area",
        signatoryX + signatureImageWidth / 2,
        signatureImageY + signatureImageHeight / 2,
        { align: "center" }
      );
    }

    doc.text("Authorised Signatory", signatoryX, authorisedTextY);
    currentContentY += signatoryBlockHeight;

    currentContentY = checkAndAddPage(currentContentY, paddingAfterElement * 2);

    doc.setFontSize(8);
    doc.setFont("helvetica", "bold");
    const factoryTitle = "FACTORY:";
    const factoryTitleDim = doc.getTextDimensions(factoryTitle);

    doc.setFont("helvetica", "normal");
    const factoryIndent = leftMargin + factoryTitleDim.w + 2;
    const factoryMaxWidth = pageWidth - factoryIndent - rightMargin;
    const factoryLines = doc.splitTextToSize(data.factory, factoryMaxWidth);
    const factoryLineHeight = (8 / 72) * 25.4 * 1.15;
    const factoryContentBlockHeight = factoryLines.length * factoryLineHeight;

    const requiredFactoryHeight = Math.max(
      factoryTitleDim.h,
      factoryContentBlockHeight
    );
    currentContentY = checkAndAddPage(currentContentY, requiredFactoryHeight);

    let factoryStartY = currentContentY;
    const baseFactoryTextY = factoryStartY + factoryLineHeight / 2;

    doc.setFontSize(8);
    doc.setFont("helvetica", "bold");
    doc.text(factoryTitle, leftMargin, baseFactoryTextY);
    doc.setFont("helvetica", "normal");
    doc.text(factoryLines, factoryIndent, baseFactoryTextY);

    if (!didAutoTableDrawFooterOnLastPage) {
      addPageNumber(doc, currentPageNumber);
    }

    console.log("PDF generation complete.");
    return doc;
  } catch (err) {
    console.error("ERROR DURING PDF GENERATION:", err);
    if (err instanceof Error) {
      console.error("Error Name:", err.name);
      console.error("Error Message:", err.message);
      console.error("Error Stack:", err.stack);
    }
    return null;
  }
};